mapper.select:
select a.article_id,a.title,a.description,a.post_time,a.view_count,a.comment_count,a.like_count,a.tag,
u.nickname as "user.nickname",u.user_id as "user.user_id",u.photo_src as "user.photo_src",u.url as "user.url"
from t_article a,t_user u
where a.user_id=u.user_id

    and u.user_id=#{userId}


    and a.cate_id=#{cate}

order by post_time desc
limit #{start},#{count}

mapper.update:
UPDATE t_article
SET `comment_count` = comment_count + #{count}
WHERE article_id = #{articleId}

UPDATE t_article
SET cate_id = #{category.cateId}, title = #{title}, description = #{sescription}, content = #{content},
    tag     = #{tag}
WHERE article_id = #{articleId}

mapper.select:
SELECT a.article_id AS "article.articleId", a.title AS "article.title", u.user_id AS "user.userId", u.nickname AS "user.nickname" FROM t_like l, t_article a, t_user u WHERE l.user_id = u.user_id AND l.article_id = a.article_id
AND l.user_id = #{userId}
AND l.article_id = #{articleId}

mapper.update:
UPDATE t_article
SET `like_count` = like_count + #{count}
WHERE article_id = #{articleId}

UPDATE t_article
SET `view_count` = view_count + #{count}
WHERE article_id = #{articleId}
